package com.syntax.class01;

public class DataTypesAndVariables {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		/*
		 *  byte - data type
		 *  variable - variable/box/container name
		 *  = assign
		 *  10 - value
		 */
		
		//storing whole numbers in variables
		
		byte variable=10; // from -128 to 127
		short variable1=100; // -32768 to 32767
		int var3=10000; // -212147483648 to 2147483647 MOST POPULAR
		long var4=1000000000; // -9,223,372,036,854,775,808 to 9,223,372,036,854,775,807
		
		//storing numbers with decimals
		
		float myfloat =10.99f;
		double myDouble=100.99; //most useful
		
		//storing single character
		
		char singleLetter='J';
		char specialcharacter='$';
		
		//storing boolean values: yes or no
		
		boolean myBoo=true;
		boolean myBoo2=false;
		
		System.out.println(variable);
		System.out.println("variable");
				
		boolean myBoo1=true;
		
		System.out.println(myBoo1);
		
		
		
				
	}

}
