package com.syntax.class02;

public class MultipleCharachters {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		char singleCharachter = 'c';

		String name = "Andrei";
		String lastName = "Smith";

		int number = 123;
		String anything = "123";

		String anything1 = "Hello my friends";

		String oneLetter = "c";
		String oneLetterAndSpace = "c ";

		// String b=true; - Mismatch, boolean type

		String str = "true";

		int phone = 1234567890;
		String phone1 = "123-456-7890";

		String sentence = "Hello World";

		System.out.println(sentence);

		int age = 22;
		System.out.println(age);

		// My age is 22

		System.out.println("My age is " + age);
		System.out.println("My name is " + name);

		String country = "Spain";
		String capital = "Madrid";

		System.out.println("The capital of " + country + " is " + capital);

		String item = "shirt";
		double $price = 19.99;
		char sign = '$';

		System.out.println("The " + item + " price is " + sign + $price);

		String number2 = "100";
		String number3 = "200";

		System.out.println(number2 + number3); // Output = 100200

		int day = 11;
		String month = "July";
		int year = 2021;
		
		System.out.println(day + "|" + month + "|" + year);
		

	}

}
