package com.syntax.class06;

public class SwitchExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		char gender = 'M';
		
		switch(gender) {
		
		case 'M':
			System.out.println("Male");
			break;
		case 'F':
			System.out.println();
			break;
		default:
			System.out.println("Gender is unknown");
		}

	}

}
