package com.syntax.class07;

public class ClassTask {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int i=1;
		
		while (i<=100) {
			System.out.print(i + " ");
			i++;
		}
		
		System.out.println("----------------------------------------");
		
		int x=100;
		
		while (x>=1) {
			System.out.println(x);
			x--;
		}
		
		System.out.println("----------------------------------------");
		
		int y=20;
		
		while (y<=100) {
			if (y%2==0)
			System.out.println(y);
			y++;
		}
		System.out.println("----------------------------------------");
		
		int z=100;
		
		while (z>=1) {
			if (z%2!=0)
			System.out.println(z);
			z--;
		}
		
		

	}

}
