package com.syntax.class07;

public class DoWhileLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		System.out.println("------------- while----------");
		
		int i=1;
		
		while (i<=3) {
			System.out.println("Hello");
			i++;
		}
		
		System.out.println("--------do while-------");
		
		int j=1;
		
		do {
			System.out.println("Hello");
			j++;
		} while (j<=3);
		

	}

}
