package com.syntax.class09;

public class Training01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// Print 1 till 9
		
		for (int i=9; i>=1; i-- ) {
		for (int j=1; j<=i; j++) {
				System.out.print(j);
			}
			System.out.println();
		}
		for (int i=1; i<=9; i++ ) {
			for (int j=1; j<=i; j++) {
				System.out.print(j);
			}
			System.out.println();
		}

	}

}
